		ADD(ans, mult(fact[t], mult(c[t] % mod, mult(C(n + t, t), power(3, m + k - t)))));

	cout << ans << endl;

	cerr << "Time elapsed: " << clock() / (double)CLOCKS_PER_SEC << endl;
	return 0;
}

