		if (i > 1)inv[i] = -inv[MOD % i] * (MOD / i);
		finv[i] = finv[i - 1] * inv[i];
	}
}

mint com(ll a, ll b) {
	if (a < 0 || b < 0 || a < b) { return 0; }
	return f[a] * finv[b] * finv[a - b];
}


int main() {
	ll N, M, K;
	cin >> N >> M >> K;

	ll S = N + M + K;
	cominit(S + 1);

	vmint c(S + 1);
	c[0] = 1;
	repn(i, S) {
		c[i] = c[i - 1] * 2;
		c[i] -= com(i - 1, M);
		c[i] -= com(i - 1, K);
	}

	mint ans = 0;
	rep(i, S) {
		if (i < N-1) { continue; }
		mint now = com(i, N - 1) * c[i - (N - 1)] * pw(3, S - 1 - i);
		ans += now;
	}
	cout << ans << endl;
}

