// a,b ax + by = gcd(a,b)  x,y 
long long extgcd(long long a, long long b, long long &x, long long &y) {
    long long g = a;
    if(b != 0){
        g = extgcd(b, a % b, y, x);
        y -= (a / b) * x;
    }else{
        x = 1;
        y = 0;
    }
    return g;
}

// ax  gcd(a, m) (mod m)  x 
// a, m  mod m  a 
long long mod_inverse(long long a, long long m)
{
    long long x, y;
    extgcd(a, m, x, y);
    return (x % m + m) % m;
}

class FactorialCalculation
{
private:
    const int mod;
    vector<long long> factorial;
    vector<long long> invFactorial;
public:
    FactorialCalculation(int n, int mod) : mod(mod)
    {
        factorial.resize(n+1, 1);
        invFactorial.resize(n+1, 1);
        for(int i=1; i<=n; ++i){
            factorial[i] = factorial[i-1] * i % mod;
            invFactorial[i] = mod_inverse(factorial[i], mod);
        }
    }
    long long getFactorial(int n){
        return factorial[n];
    }
    long long getPermutation(int n, int r){
        if(n < r)
            return 0;
        return factorial[n] * invFactorial[n-r] % mod;
    }
    long long getCombination(int n, int r){
        if(n < r)
            return 0;
        return getPermutation(n, r) * invFactorial[r] % mod;
    }
    long long getHomogeneous(int n, int r){
        return getCombination(n+r-1, r);
    }
};

const int MOD = 1000000007;

int main()
{
    int a, b, c;
    cin >> a >> b >> c;
    FactorialCalculation f(a+b+c, MOD);

    long long tmp = 1;
    long long ans = 1;
    for(int i=1; i<=b+c; ++i){
        tmp *= 2;
        tmp %= MOD;
        if(i > b){
            tmp -= f.getCombination(i - 1, b);
            tmp += MOD;
            tmp %= MOD;
        }
        if(i > c){
            tmp -= f.getCombination(i - 1, i - 1 - c);
            tmp += MOD;
            tmp %= MOD;
        }

        ans *= 3;
        ans += tmp * f.getCombination(a + i - 1, i);
        ans %= MOD;
    }
    cout << ans << endl;

    return 0;
}

