template<int B> // n <= 1<<B
struct FFT{
  vector<int> m2[B+1];
  vector<C> power[B+1];

  FFT(){
    rep(e, B+1){
      int lim = 1 << e;
      m2[e].resize(lim);
      power[e].resize(lim);
      rep(st, lim) power[e][st] = polar(1.0, M_PI*st/lim);
      rep(i, e) rep(st, lim) if(st >> i & 1) m2[e][st] |= 1 << e-1-i;
    }
  }

  void Dft(C *f, int lim) {
    int e = 31 - __builtin_clz(lim);
    assert(e <= B);
    assert(1 << e == lim);
    rep(st, lim) if(st < m2[e][st]) swap(f[st], f[m2[e][st]]);

    rep(k, e) {
      int t = 1 << k;
      int w = 1 << k+1;
      C *po = power[k].data();
      for (int i=0; i<lim; i+=w) {
        rep(j, t) {
          C x = f[i+j];
          C y = f[i+j+t] * po[j];
          f[i+j] = x + y;
          f[i+j+t] = x - y;
        }
      }
    }
  }

  template <class T>
  vector<C> Conv(vector<T> &f, vector<T> &g) {
    int n = f.size() + g.size() - 1;
    int lim = 2;
    while (lim < n) lim <<= 1;
    vector<C> cf(all(f));
    vector<C> cg(all(g));
    cf.resize(lim);
    cg.resize(lim);
    Dft(cf.data(), lim);
    Dft(cg.data(), lim);
    rep(i, lim) cf[i] = conj(cf[i] * cg[i])/double(lim);
    Dft(cf.data(), lim);
    // DO conj one more if you put complex numbers !!!
    cf.resize(n);
    return cf;
  }

  vector<LL> Conv2(vector<LL> &f, vector<LL> &g, LL mod) {
    int n = f.size();
    int m = g.size();
    int lim = 2;
    while (lim < n+m) lim <<= 1;

    const int D = 3;
    const int L = 10;
    const int M = (1 << L) - 1;
    vector<C> cs[D];
    vector<C> ds[D];
    vector<C> es[D];
    rep(i, D) {
      cs[i].resize(lim+10);
      ds[i].resize(lim+10);
      es[i].resize(lim+10);
      rep(j, n) es[i][j].real(f[j] >> (i*L) & M);
      rep(j, m) es[i][j].imag(g[j] >> (i*L) & M);
      Dft(es[i].data(), lim);
      rep(j, lim) {
        int nj = (lim-j) & (lim-1);
        cs[i][j] = es[i][j] + conj(es[i][nj]);
        ds[i][j] = es[i][j] - conj(es[i][nj]);
      }
      es[i].assign(lim, C());
    }

    rep(i, D) {
      rep(j, D) {
        rep(k, lim) {
          int r = (i+j) >> 1;
          int m = (i+j) % 2;
          es[r][k] += cs[i][k] * ds[j][k] * polar(0.25, (m-1) * M_PI/2);
        }
      }
    }

    rep(i, D) {
      rep(j, lim) es[i][j] = conj(es[i][j])/double(lim);
      Dft(es[i].data(), lim);
    }

    vector<LL> ret(n+m-1);
    rep(j, ret.size()) {
      LL mul = 1;
      rep(i, D*2-1) {
        LL v;
        if (i%2) {
          v = llround(-es[i/2][j].imag()) % mod;
        } else {
          v = llround(es[i/2][j].real()) % mod;
        }
        ret[j] += v * mul;
        ret[j] %= mod;
        mul <<= L;
        mul %= mod;
      }
    }
    return ret;
  }
};
