  assert(freopen("in", "rt", stdin));
  auto _clock_start = chrono::high_resolution_clock::now();
#endif
  ios::sync_with_stdio(false);
  cin.tie(nullptr);

  int n, m, k;
  cin >> n >> m >> k;
  int const MAX = n + m + k + 10;
  vector<mint> fact(MAX);
  fact[0] = 1;
  for (int i = 1; i < MAX; ++i) {
    fact[i] = fact[i - 1] * i;
  }
  vector<mint> ifact(MAX);
  ifact.back() = inv(fact.back());
  for (int i = MAX - 2; i >= 0; --i) {
    ifact[i] = ifact[i + 1] * (i + 1);
  }
  auto choose = [&](int N, int M) -> mint {
    if (M > N || M < 0 || N < 0) {
      return 0;
    }
    return fact[N] * ifact[M] * ifact[N - M];
  };
  vector<mint> pw(MAX);
  pw[0] = 1;
  for (int i = 1; i < MAX; ++i) {
    pw[i] = pw[i - 1] * 3;
  }
  mint ans;
  --n;
  mint cur = 0;
  int L, R;
  for (int s = 0; s <= m + k; ++s) {
    auto coef = pw[m + k - s] * choose(n + s, s);
    if (s == 0) {
      cur = 1;
      L = 0;
      R = 0;
    } else {
      cur += cur;
      cur -= choose(s - 1, R);
      cur += choose(s - 1, L - 1);
      if (s > k) {
        cur -= choose(s, L++);
      }
      if (s <= m) {
        cur += choose(s, ++R);
      }
    }
    ans += coef * cur;
  }
  cout << ans << '\n';

