    if(p == 0) {return 1;}
    ll ans = fpow(x, p / 2);
    ans = (ans * ans ) % mod;
    if(p & 1) {
        return (ans * x) % mod;
    } else {
        return ans;
    }
}

ll comb(ll k, ll n) {
    if(k < 0 || n < 0) {return 0;}
    if(n - k < 0) {return 0;}
    return (((perm[n] * inv[n - k]) % mod) * inv[k]) % mod;
}

