int sub (int x, int y) {x -= y; if (x < 0) return x + MOD; return x;}

int a, b, c, sol;
int fct[MAXN], inv[MAXN];

int pot (int a, int e) {
    int res = 1;
    while (e > 0) {
        if (e & 1) res = mul(res, a);
        a = mul(a, a);
        e /= 2;
    }
    return res;
}

void precompute () {
    fct[0] = inv[0] = 1;
    for (int i = 1; i < MAXN; i++) {
        fct[i] = mul(fct[i - 1], i);
        inv[i] = pot(fct[i], MOD - 2);
    }
}

int nck (int n, int k) {
    return mul(fct[n], mul(inv[n - k], inv[k]));
}

void calc () {
    if (b > c) swap(b, c);
    int curr = 0, uk = 1;
    for (int len = 0; len <= b + c; len++) {
        int val = sub(uk, curr);
        val = mul(val, nck(len + a - 1, a - 1));
        val = mul(val, pot(3, b + c - len));
        sol = add(sol, val);
        uk = sub(add(uk, uk), nck(len, min(len, b)));
        if (len < b) uk = add(uk, 1);
        if (len == c) {
            curr = 1;
        } else if (len > c) {
            curr = add(sub(add(curr, curr), nck(len, len - c - 1)), nck(len + 1, len - c));
        }
    }
}

