            Mult(c, 2);
            if(i - 1 >= M + 1) Add(c, C.nCr(i - 1, M + 1));
            if(i - 1 - K - 1 >= 0) Add(c, C.nCr(i - 1, i - 1 - K - 1));
            if(i >= M + 1) Add(c, mod - C.nCr(i, M + 1));
            if(i - K - 1 >= 0) Add(c, mod - C.nCr(i, i - K - 1));
        }
        int tmp = (c * C.nCr(N - 1 + i, i)) % mod;
        Add(ans, tmp * modpow(3, M + K - i, mod));
    }
    cout << ans << endl;

    return 0;
}

