int main(){
  scanf("%d%d%d",&n,&m,&k);
  assert(n<=1000 && m<=1000 && k<=1000);
  // fact init
  fact[0] = 1;
  FOR(i,1,FMAX)fact[i]=fact[i-1]*i%MOD;
  ifact[FMAX-1]=modinv(fact[FMAX-1],MOD);
  FORR(i,0,FMAX-1)ifact[i]=ifact[i+1]*(i+1)%MOD;
  // tri init
  tri[0] = 1;
  FOR(i,1,FMAX)tri[i]=tri[i-1]*3%MOD;
  // a == n-1 && b <= m && c <= k
  ll ans = 0;
  int a = n-1;
  FOR(s,0,m+k+1){
    // c = s-b
    // 0<=c<=k
    // 0<=b<=m

    // s-b>=0 -> b<=s
    // s-b<=k -> b>=s-k
    int abc = a+s;
    int rest = n+m+k-abc-1;
    ll unit = fact[abc]*ifact[a]%MOD*ifact[s]%MOD*tri[rest]%MOD;
    ll add = 0;
    int beg = max(s-k,0);
    int end = min(s,m);
    FOR(b,beg,end+1){
      int c = s-b;
      add += fact[b+c]*ifact[b]%MOD*ifact[c]%MOD;
      if(add>=MOD)add-=MOD;
    }
    ans += unit*add%MOD;
    if(ans>=MOD)ans-=MOD;
  }
  printf("%lld\n",ans);
  return 0;
}

