vector<mint> fact, factinv;
void nCr_computeFactinv(int N) {
	fact.resize(N + 1); factinv.resize(N + 1);
	fact[0] = 1;
	rer(i, 1, N) fact[i] = fact[i - 1] * i;
	factinv[N] = fact[N].inverse();
	for(int i = N; i >= 1; i --) factinv[i - 1] = factinv[i] * i;
}
mint nCr(int n, int r) {
	return r > n ? 0 : fact[n] * factinv[n - r] * factinv[r];
}

int main() {
	mint::set_mod((int)1e9+7);
	int N; int M; int K;
	while(~scanf("%d%d%d", &N, &M, &K)) {
		nCr_computeFactinv(N + M + K);
		mod_polynomial::Polynomial p, q;
		p.resize(M + 1), q.resize(K + 1);
		rep(i, M + 1) p.set(i, factinv[i]);
		rep(i, K + 1) q.set(i, factinv[i]);
		auto pq = p * q;
		mint ans;
		rer(mk, 0, M + K) {
			mint sum = pq.get(mk);
			sum *= fact[N - 1 + mk];
			sum *= factinv[N - 1];
			ans = ans * 3 + sum;
		}
		printf("%d\n", ans.get());
	}
	return 0;
}

