    for (i = 0; i <= m; i++) {
        for (j = 0; j <= k; j++) {
            int c = n - 1 + m - i + k - j;

            ans += a[i + j] * comb(c, n - 1, mod) % mod * comb(c - n + 1, m - i, mod) % mod;
        }
    }

    printf("%lld\n", ans % mod);

    return 0;
}

