		if(i < n)C[t][i] = 0;
		else if(i == n)C[t][i] = 1;
		else {
			ll ret = C[t][i-1];
			ret *= i; ret %= M;
			ret *= modpow(i-n,M-2); ret %= M;
			C[t][i] = ret;
