typedef complex <double> cd;
const int K = 20, MOD = 1e9 + 7, C = 31622;
const double PI = acos(-1);
vector <cd> w[K + 1];
int lg[(1 << K) + 1];

void fft(vector <cd> & a, int inv) {
	int n = a.size();

	int j = 0;
	for (int i = 1; i < n; i++) {
		int k = n >> 1;
		for (; j & k; k >>= 1) {
			j ^= k;
		}
		j ^= k;
		if (i < j) {
			swap(a[i], a[j]);
		}
	}
	cd even, odd;
	for (int len = 2; len <= n; len <<= 1) {
		int m = lg[len];
		for (int i = 0; i < n; i += len) {
			for (int j = 0; j < len / 2; j++) {
				even = a[i + j], odd = a[i + j + len / 2];
				if (inv) {
					even /= 2, odd /= 2;
					odd *= w[m][j == 0 ? 0 : len - j];
				}	else {
					odd *= w[m][j];
				}
				a[i + j] = even + odd;
				a[i + j + len / 2] = even - odd;
			}
		}
	}
}

vll mul(const vi & a, const vi & b) {
	vector <cd> fa(all(a)), fb(all(b));
	int n = 1, need = a.size() + b.size();
	while (n < need) {
		n <<= 1;
	}
	fa.resize(n);
	fb.resize(n);
	fft(fa, 0);
	fft(fb, 0);
	for (int i = 0; i < n; i++) {
		fa[i] *= fb[i];
	}
	fft(fa, 1);
	vll res(need - 1);
	for (int i = 0; i < need - 1; i++) {
		res[i] = llround(fa[i].real());
	}
	return res;
}

vi conv(const vi & a, const vi & b) {
	int A = a.size(), B = b.size();
	vi a1(A), a2(A);
	for (int i = 0; i < A; i++) {
		a1[i] = a[i] % C;
		a2[i] = a[i] / C;
	}
	vi b1(B), b2(B);
	for (int i = 0; i < B; i++) {
		b1[i] = b[i] % C;
		b2[i] = b[i] / C;
	}
	vll c11 = mul(a1, b1);
	vll c12 = mul(a1, b2);
	vll c21 = mul(a2, b1);
	vll c22 = mul(a2, b2);

	vi res(A + B - 1);
	for (int i = 0; i < A + B - 1; i++) {
		res[i] = c11[i] % MOD;
		res[i] += (ll)C * ((c12[i] + c21[i]) % MOD) % MOD;
		if (res[i] >= MOD) {
			res[i] -= MOD;
		}
		res[i] += (ll)C * C * (c22[i] % MOD) % MOD;
		if (res[i] >= MOD) {
			res[i] -= MOD;
		}
	}
	return res;
}

void pre_fft() {
	for (int i = 2; i <= 1 << K; i++) {
		lg[i] = lg[i / 2] + 1;
	}
	for (int i = 0; i <= K; i++) {
		w[i].resize(1 << i);
		double ang = 2 * PI / (1 << i);
		for (int j = 0; j < 1 << i; j++) {
			w[i][j] = cd(cos(j * ang), sin(j * ang));
		}
	}
}

