const int P = 1000000007;
int inv[MN], frac[MN], p3[MN];
inline int mul(int x, int y){return (ll)x * y % P;}
inline int plu(int x, int y){return (x += y) >= P ? x - P : x;}
inline int fpw(int x, int k){
	R int res = 1;
	for (; k; k >>= 1, x = mul(x, x)) 
		if (k & 1) res = mul(res, x);
	return res;
}
inline int C(int n, int m){if (m < 0 || m > n) return 0; return mul(frac[n], mul(inv[m], inv[n-m]));}
int n, m, k, res = 1, ans = 0;
