	n = read(), m = read(), k = read();
	p3[0] = frac[0] = 1;
	for (R int i = 1; i <= m + k; ++i) p3[i] = mul(p3[i - 1] , 3);
	for (R int i = 1; i <= n + m + k; ++i) frac[i] = mul(frac[i - 1], i);
	inv[n + m + k] = fpw(frac[n + m + k], P - 2);
	for (R int i = n + m + k; i--; ) inv[i] = mul(inv[i + 1], i + 1);
	for (R int i = n; i <= n + m + k; ++i) {
		ans = plu(ans, mul(p3[n + m + k - i], mul(C(i - 1, n - 1), res)));
		res = plu(plu(res, res), plu(plu(-C(i - n, i - n -k), P), plu(-C(i - n, m), P)));
	}
