long long extgcd(long long a, long long b, long long& x, long long& y) {
        long long d = a;
        if (b != 0) {
                d = extgcd(b, a % b, y, x);
                y -= (a / b) * x;
        } else {
                x = 1;
                y = 0;
        }
        return d;
}

long long inv(long long a) {
        long long x, y;
        extgcd(a, MOD, x, y);
        return (x % MOD + MOD) % MOD;
}

long long nCr(long long n, long long r) {
        if (n < r) return 0;
        if (n - r < r) r = n - r;
        long long ret = 1;
        rep(i, r) {
                ret *= n;
                -- n;
                ret %= MOD;
                ret *= inv(i + 1);
                ret %= MOD;
        }
        return ret;
}

long long mod_pow(long long x, long long n) {
        long long res = 1;
        while (n > 0) {
                if (n & 1) res = res * x % MOD;
                x = x * x % MOD;
                n >>= 1;
        }
        return res;
}

