    for (ll i = 1; i <= s; i++) {
        if (i <= m && i <= k) {
            dp[i] = dp[i-1]*2%MOD;
        } else if (i <= m) {
            dp[i] = (dp[i-1]*2-conb(i-1,i-1-k)+MOD*MOD)%MOD;
        } else if (i <= k) {
