const int N = 900005;
const int md = 1e9 + 7;

int n, m, k;
int fact[N], inv[N], pw3[N];
int ans;

int pw(int a, int b) {
	int res = 1;
	while(b) {
		if (b & 1) res = 1LL * res * a % md; a = 1LL * a * a % md;
		b >>= 1;
	}
	return res;
}

int C(int n, int k) {
	if (n == k) return 1;
	if (n < 0 || k < 0 || n < k) return 0;
	return 1LL * fact[n] * (1LL * inv[k] * inv[n - k] % md) % md;
}

int main() {
	ios_base::sync_with_stdio(false); cin.tie(0);
	cin >> n >> m >> k;

	pw3[0] = 1; for (int i = 1; i < N; ++i) pw3[i] = 3LL * pw3[i - 1] % md;
