	for (int i = 0; i < N; ++i) inv[i] = pw(fact[i], md - 2);

	int f = 0;
	for (int i = n; i <= n + m + k; ++i) {
		if (i == n) {
			for (int j = i - n - k; j <= m; ++j) f = (f + C(i - n, j)) % md;
		} else {
			f = 2LL * f % md; 
			f = (f - C(i - 1 - n, i - 1 - n - k)) % md; if (f < 0) f += md;
			f = (f - C(i - 1 - n, m)) % md; if (f < 0) f += md;
 		}
