class modint {
	//MOD
	//
private:
	static const int MOD = 1000000007;
public:
	modint() {
		//assert(is_prime(MOD));
		this->number = 0;
	}
	modint(const int src) {
		//assert(is_prime(MOD));
		this->number = opposit(src);
	}
	modint(const modint &src) {
		this->number = src.number;
	}

	modint& operator += (const modint& obj) {
		this->number = san2(this->number + obj.number);
		return *this;
	}
	modint& operator -= (const modint& obj) {
		this->number = san2(this->number - obj.number + MOD);
		return *this;
	}
	modint& operator *= (const modint& obj) {
		this->number = (this->number * obj.number) % MOD;
		return *this;
	}
	modint& operator /= (const modint& obj) {
		this->number = (this->number * inverse(obj.number)) % MOD;
		return *this;
	}
	modint& operator += (const int n) {
		this->number = san2(this->number + opposit(n));
		return *this;
	}
	modint& operator -= (const int n) {
		this->number = san2(this->number - opposit(n) + MOD);
		return *this;
	}
	modint& operator *= (const int n) {
		this->number = (this->number * opposit(n)) % MOD;
		return *this;
	}
	modint& operator /= (const int n) {
		this->number = (this->number * inverse(n)) % MOD;
		return *this;
	}

	modint operator + (const modint obj) { modint re(*this); return re += obj; }
	modint operator - (const modint obj) { modint re(*this); return re -= obj; }
	modint operator * (const modint obj) { modint re(*this); return re *= obj; }
	modint operator / (const modint obj) { modint re(*this); return re /= obj; }
	modint operator + (const int n) { modint re(*this); return re += n; }
	modint operator - (const int n) { modint re(*this); return re -= n; }
	modint operator * (const int n) { modint re(*this); return re *= n; }
	modint operator / (const int n) { modint re(*this); return re /= n; }

	modint operator = (const int n) {
		this->number = opposit(n);
		return *this;
	}
	int get() {
		return number;
	}

private:
	int number;

	int opposit(int n) {
		if (n < 0)n = MOD - ((-n) % MOD);
		return n % MOD;
	}
	int inverse(int n) {
		n = opposit(n);
		int result = 1;
		for (int i = MOD - 2; i; i /= 2) {
			if (i % 2)result = (result * n) % MOD;
			n = (n * n) % MOD;
		}
		return result;
	}
	inline int san2(const int n) {
		return MOD <= n ? n - MOD : n;
	}
	bool is_prime(int n) {
		if (n <= 1)return false;
		if (n == 2)return true;
		if (n % 2 == 0) return false;
		const int upperbound = int(sqrt(n));
		for (int i = 3; i <= upperbound; i += 2) {
			if (n % i == 0) return false;
		}
		return true;
	}
};
modint power(modint n, int p) {
	modint result = 1;
	for (; p; p /= 2) {
		if (p % 2)result *= n;
		n *= n;
	}
	return result;
}
modint power(int n, int p) {
	modint mn = n;
	return power(mn, p);
}

struct combi {
public:
	vector<modint>facto;
	combi(const int N) :facto(N) {
		facto[0] = 1;
		REP(i, 1, N)facto[i] = facto[i - 1] * i;
	}
	modint get_combi(const int N, const int C) {
		//combination(N+C,C)
		if (facto.size() <= N + C) {
			int a = facto.size();
			facto.resize(N + C + 1);
			REP(i, a, N + C + 1)facto[i] = facto[i - 1] * i;
		}
		return facto[N + C] / (facto[N] * facto[C]);
	}
};

signed main(void) {
	cin.tie(0);
	ios::sync_with_stdio(false);

	combi c(1000010);

	int N, M, K;
	cin >> N >> M >> K;
	if (M > K) swap(M, K);

	modint pre = 1, p3 = power(3, M + K), ans = p3;

	//N+iNa
	REP(i, 1, M + K + 1) {
		pre *= 2;
		p3 /= 3;
		if (i > M) {
			pre -= c.get_combi(M, i - M - 1);
			if (i > K) pre -= c.get_combi(K, i - K - 1);
		}
		ans += pre * c.get_combi(N - 1, i) * p3;
	}

	cout << ans.get() << endl;

	return 0;
}

