struct base {

    double a, b;

    base() : a(0), b(0) {}

    base(double a, double b = 0) : a(a), b(b) {}

};

inline base operator + (const base& a, const base& b) {
    return base(a.a + b.a, a.b + b.b);
}

inline base operator - (const base& a, const base& b) {
    return base(a.a - b.a, a.b - b.b);
}

inline base operator * (const base& a, const base& b) {
    return base(a.a * b.a - a.b * b.b, a.a * b.b + a.b * b.a);
}

inline base operator / (const base& a, double b) {
    return base(a.a / b, a.b / b);
}

int maxv;
base a[K][N], ab[K * 2][N];
int f[N], rf[N];

inline int calc(int a, int b) {
    int res = 1;
    while (b > 0) {
        if (b % 2 != 0) {
            res = (1LL * res * a) % MOD;
        }
        a = (1LL * a * a) % MOD;
        b /= 2;
    }
    return res;
}

inline void fft(base* a, int n, bool fl) {
    for (int i = 1, j = 0; i < n; ++i) {
        int bit = n / 2;
        for (; j >= bit; bit /= 2) {
            j -= bit;
        }
        j += bit;
        if (i < j) {
            swap(a[i], a[j]);
        }
    }
    for (int len = 2; len <= n; len *= 2) {
        double ang = 2 * pi / len * (fl ? -1 : 1);
        base wlen(cos(ang), sin(ang));
        for (int i = 0; i < n; i += len) {
            base w(1);
            for (int j = 0; j < len / 2; ++j) {
                base u = a[i + j], v = a[i + j + len / 2] * w;
                a[i + j] = u + v;
                a[i + j + len / 2] = u - v;
                w = w * wlen;
            }
        }
    }
    if (fl) {
        for (int i = 0; i < n; ++i) {
            a[i] = a[i] / n;
        }
    }
}

