inline void dft(int n, int kier)
{
    int n2=n-1;
    int s=0;
    int p;
    int g;

    for (int i=2; i<=n; i<<=1)
    {

        for (int j=0; j<n; j++)
        {
            dfts[j]=dftn[j];
            dftn[j]=0;
        }

        p=n/i;

        if (kier)
        {
            g=0;
            for (int j=0; j<n; j++)
            {
                dftn[j]=dfts[(2*(j-s)+s)&n2]+omega[g]*dfts[(2*(j-s)+p+s)&n2];
                s++;
                if (s==p)
                {
                    g=j+1;
                    s=0;
                }
            }
        }
        else
        {
            g=n;
            for (int j=0; j<n; j++)
            {
                dftn[j]=dfts[(2*(j-s)+s)&n2]+omega[g]*dfts[(2*(j-s)+p+s)&n2];
                s++;
                if (s==p)
                {
                    g=n-j-1;
                    s=0;
                }
            }
        }
    }
}

