const int N = 1000005;
const int mod = 1e9+7;

int n, fact[N], inv[N];

int pw(int a, int n) {
    int result = 1;
    while (n > 0) {
        if (n & 1)
            result = (1LL * result * a) % mod;
        a = (1LL * a * a) % mod;
        n /= 2;
    }
    return result;
}

void precalc() {
    fact[0] = inv[0] = 1;
    for (int i = 1; i < N; ++i) {
        fact[i] = (1LL * fact[i - 1] * i) % mod;
        inv[i] = pw(fact[i], mod - 2);
    }
}

int C(int n, int k) {
    if (k < 0 || k > n) return 0;
    int result = fact[n];
    result = (1LL * result * inv[n - k]) % mod;
    result = (1LL * result * inv[k]) % mod;
    return result;
}

