    precalc();
    int a, b, c;
    cin >> a >> b >> c;
    int result = 0;
    int prv = 0, pl, pr;
    for (int len = a; len <= a + b + c; ++len) {
        int n = len - a;
        int cur = 0;
        int l = max(n - c, 0), r = min(b, n);
        if (len == a) {
            cur = 1;
        } else {
            cur = (2 * prv) % mod;
            cur = (cur - C(n - 1, pl) + mod) % mod;
            cur = (cur - C(n - 1, pr) + mod) % mod;
            if (pl == l) cur = (cur + C(n, l)) % mod;
            if (pr < r) cur = (cur + C(n, r)) % mod;
        }
        prv = cur;
        pl = l;
        pr = r;
        cur = (1LL * cur * C(len - 1, a - 1)) % mod;
        cur = (1LL * cur * pw(3, a + b + c - len)) % mod;
        result = (result + cur) % mod;
    }
    cout << result << endl;
    return 0;
}

