using namespace std;

const int mod = 1'000'000'007;
int fact[1'000'000], ifact[600'001];

int main()
{
	int a, b, c;
	cin >> a >> b >> c;
	--a;

	fact[0] = 1;
	for (int i = 1, max = a + b + c; i <= max; ++i)
		fact[i] = (i * (long long)fact[i - 1]) % mod;

	int imax = b + c; if (a > imax) imax = a;
	long long cur = fact[imax];
	int pow = mod - 2, inv = 1;
	while (pow > 0) {
		if (pow & 1) inv = (inv * cur) % mod;
		pow >>= 1;
		cur = (cur*cur) % mod;
	}
	for (ifact[imax] = inv; imax > 0; --imax)
		ifact[imax - 1] = (imax * (long long)ifact[imax]) % mod;

	unsigned int sum = 1, res = 1;
	for (int i = 1, max = b + c; i <= max; ++i) {
		sum = 2 * (sum + mod);
		if (i > b) sum -= ((long long)fact[i - 1] * ifact[b] % mod) * ifact[i - b - 1] % mod;
		if (i > c) sum -= ((long long)fact[i - 1] * ifact[c] % mod) * ifact[i - c - 1] % mod;
		sum %= mod;
		res *= 3;
		res += sum * (long long)fact[a + i] % mod * ifact[a] % mod * ifact[i] % mod;
		res %= mod;
	}
	cout << res << endl;
}

