	for(int i=0; i<=n; ++i){
		comb[i][0] = 1;
		for(int j=1; j<=i; ++j){
			comb[i][j] = comb[i-1][j-1] + comb[i-1][j];
			comb[i][j] %= DIV;
		}
	}
