using ll = long long;

struct Mod
{
    static const auto P = static_cast<int>(1e9+7);
    ll k;

    Mod() = default;
    Mod(const ll k) : k(k%P) {}
};

Mod operator+(const Mod a, const Mod b) { return a.k + b.k; }
Mod operator*(const Mod a, const Mod b) { return a.k * b.k; }
Mod& operator+=(Mod& a, const Mod b) { return a = a+b; }

Mod cache[4000][4000];
bool seen[4000][4000];
Mod choose(int n, int k)
{
    if (seen[n][k]) return cache[n][k];
    seen[n][k] = true;

    auto& result = cache[n][k];
    if (k == 0 || n == k) result = 1;
    else if (k > n) result = 0;
    else result = choose(n-1, k-1) + choose(n-1, k);

    return result;
};

