	for(int i=N;i;i--)
		inv[i-1]=1ll*inv[i]*i%mo;
	int ans=0,nowans=1;
	for(int l=n+1;l<=N+1;l++)
	{
		ans+=1ll*C(l-2,n-1)*nowans%mo*Pow3[N+1-l]%mo;
		if(ans>=mo)ans-=mo;
		nowans=nowans*2%mo;
		nowans-=(C(l-(n+1),m)+C(l-(n+1),k))%mo;
		if(nowans<0)nowans+=mo;
	}
	printf("%d\n",ans);
}

