		int *fact, *factInv;
		void factInit(int N, int mod=MOD) {
			fact = new int[N]; factInv = new int[N];
			fact[0] = 1;
			upto(1,N,i) fact[i] = fact[i-1] * i % mod;
			factInv[N] = mpow(fact[N], mod-2, mod);
			rtimes(N,i) factInv[i] = factInv[i+1] * (i+1) % mod;
		}
	/* </sr.m.fact> */
	// NOT_VERIFIED
	// O(1), before:O(N)
	inline int nCk(int n, int k, int mod=MOD) {
		if(!fact) { cerr << "factInit before nCk.\n"; return -1; }
		return fact[n] * factInv[k] % mod * factInv[n-k] % mod;
	}
/* </sr.m.nck> */

