			int v = e[ i ].to ;
			if( d[ v ] > d[ u ] + e[ i ].v ) {
				d[ v ] = d[ u ] + e[ i ].v ;
				if( !vis[ v ] ) vis[ v ] = 1 , q[ r ++ ] = v ;
			}
