  for(int i = 1; i < 1010101; i++) pow3[i] = pow3[i - 1] * 3 % mod;

  pair< int, int > ask[2][1010101];

  auto get = [&](int x, int y, bool t) {
    if(x == 0) {
      int64 ret = 0;
      for(int j = 0; j <= y; j++) {
        ret += beet.C(x, j);
        ret %= mod;
      }
      ask[t][x] = {y, ret};
      return ret;
    } else {
      int64 ret = ask[t][x - 1].second * 2 % mod;
      ret += mod - beet.C(x - 1, ask[t][x - 1].first);
      ret %= mod;
      while(y < ask[t][x - 1].first) {
        ret += mod - beet.C(x, ask[t][x - 1].first);
        ret %= mod;
        --ask[t][x - 1].first;
      }
      while(ask[t][x - 1].first < y) {
        ++ask[t][x - 1].first;
        ret += beet.C(x, ask[t][x - 1].first);
        ret %= mod;
      }
      ask[t][x] = {y, ret};
