		for(l=2,c=1;l<=n;l<<=1,c++){
			if(dft==-1)_w=iww[c];else _w=ww[c];
			for(i=l>>1,j=0;j<n;j+=l)for(k=0;k<i;k++){
				u=a[j+k],v=a[j+k+i]*_w[k];
				a[j+k]=u+v,a[j+k+i]=u-v;
			}
		}
	}
