			tx[i]=p0*q0,ty[i]=p0*q1+p1*q0+p1*q1.mult();
		}
		mmp(x,tx,n),mmp(y,ty,n);
		fft(x,n,-1),fft(y,n,-1);
		for(int i=0;i<n;i++){
			p0=x[i],p1=y[i];
