            sum = (1LL * sum * P + ab[j][i]) % MOD;
        }
        sum = (1LL * sum * pw3) % MOD;
        sum = (1LL * sum * f[n - 1 + i]) % MOD;
        ans += sum;
        if (ans >= MOD) {
            ans -= MOD;
        }
        pw3 = (1LL * inv3 * pw3) % MOD;
    }
    ans = (1LL * ans * calc(3, m + k)) % MOD;
    ans = (1LL * ans * rf[n - 1]) % MOD;
    cout << ans << endl;
    return 0;
}

