void solve() {
  ll ans = 0;
  ll v1 = 1, v2 = 1, tmp1 = 1, tmp2 = 1;
  for (int len = N ; len <= N+M+K ; len++) {
    cerr << "Len = " << len << "\n";
    int bad = len - N;
    if (len == N) {
      v1 = 1;
    } else {
      v1 = modSum(v1, v1);
    }
    if (bad-1 >= M) {
      if (bad-1 > M) {
        tmp1 = modProd(tmp1, modProd(bad-1, getInverse(bad-1-M)));
      }
      v1 = modSubtract(v1, tmp1);
    }
    if (bad-1 >= K) {
      if (bad-1 > K) {
        tmp2 = modProd(tmp2, modProd(bad-1, getInverse(bad-1-K)));
      }
      v1 = modSubtract(v1, tmp2);
    }
    if (len > N) {
      v2 = modProd(v2, modProd(len-1, getInverse(len-1-(N-1))));
    }
    ll mul = v1;
    mul = modProd(mul, v2);
    mul = modProd(mul, getPowMod(3, N+M+K-len));
    ans = modSum(ans, mul);
    /* 
    cerr << "tmp1 = " << tmp1 << "\n";
    cerr << "tmp2 = " << tmp2 << "\n";
