	ll ans = 0;
	ll cur = 0;
	rep(t, m + k + 1) {
		ll csum = p.comb(t + n-1, t)*t3[m+k-t]%mod;
		ll ncur = 0;
		if (t == 0) {
			cur = 1;
		}
		else {
			int ple = max(t - 1 - k, 0);
			int pri = min(m, t - 1);
			int le = max(t - k, 0);
			int ri = min(m,t);
			ncur += cur;
			cur += mod-p.comb(t - 1, ple);
			cur += p.comb(t - 1, pri + 1);
			if (le == ple)cur++;
			if (pri == ri) {
				//cur += mod - 1;
				cur += mod - p.comb(t - 1, pri);
				cur += mod - p.comb(t - 1, pri + 1);
			}
			while (cur >= mod)cur -= mod;
			ncur += cur;
			while (ncur >= mod)ncur -= mod;
			cur = ncur;
			//cout << t << " " << ncur << endl;
		}
		ans += cur * csum%mod;
		if (ans >= mod)ans -= mod;
		//cout << ans << endl;
	}
	cout << ans << endl;
}

