                    if (g[w] == 0) {
                        if (w == 0)
                            D[w][x][y][z] = pw3[y + z];
                        else
                            D[w][x][y][z] = 0;
                    } else {
                        g[w]--;
                        D[w][x][y][z] = (1ll * D[0][g[0]][g[1]][g[2]] + D[1][g[0]][g[1]][g[2]] + D[2][g[0]][g[1]][g[2]]) % MOD;
                    }
                }
            }
        }
    }
    return D[0][n][m][k];
}

int powmod(int x, int b) {
    int res = 1;
    while (b > 0) {
        if (b & 1)
            res = 1ll * res * x % MOD;
        x = 1ll * x * x % MOD;
        b >>= 1;
    }
    return res;
}

inline int inv(int x) {
    return powmod(x, MOD - 2);
}

int F[N], iF[N];

int C(int x, int y) {
    if (y < 0 || y > x || x < 0)
        return 0;
    return 1ll * F[x] * iF[y] % MOD * iF[x - y] % MOD;
}

