    if (x == 0)
        D[x] = 1;
    if (D[x] != -1)
        return D[x];
    D[x] = 2ll * D[x - 1] % MOD;
    if (x >= m + 1 && x < k + 1) {
        D[x] = (D[x] + MOD - C(x - 1, x - m - 1)) % MOD;
    } else if (x >= k + 1) {
        D[x] = (D[x] + 2ll * MOD - C(x - 1, x - m - 1) - C(x - 1, x - k - 1)) % MOD;
    }
    return D[x];
}

