    fac[0]=1;
    REP1(i,1,N-1) fac[i]=fac[i-1]*i%MOD;
    ifac[0]=1;
    REP1(i,1,N-1) ifac[i]=ifac[i-1]*inv[i]%MOD;
    pw3[0]=1;
    REP(i,N-1) pw3[i+1]=pw3[i]*3%MOD;
}

LL F( int n, int m ) {
    return fac[n+m]*ifac[m]%MOD*ifac[m]%MOD;
}

LL F( int n, int m, int k ) {
    return fac[n+m+k]*ifac[n]%MOD*ifac[m]%MOD*ifac[k]%MOD;
}

