template<int MOD>
struct ModInt {
   using Mint = ModInt;
   int val;
   ModInt(ll __val = 0) { fix(__val % MOD + MOD); }
   Mint& fix(int __val) { val = __val; if (val >= MOD) val -= MOD; return *this; }
   explicit operator int() { return val; }
   Mint operator + (const Mint &mt) const { return Mint().fix(val + mt.val); }
   Mint operator - (const Mint &mt) const { return Mint().fix(val - mt.val + MOD); }
   Mint operator * (const Mint &mt) const { return Mint().fix(ll(val) * mt.val % MOD + MOD); }
   Mint& operator += (const Mint &mt) { return *this = *this + mt; }
   Mint& operator -= (const Mint &mt) { return *this = *this - mt; }
   Mint& operator *= (const Mint &mt) { return *this = *this * mt; }
   Mint pow(int y) const {
      Mint x = *this;
      Mint ans(1);
      for (; y > 0; y >>= 1, x *= x) {
         if (y & 1) ans *= x;
      }
      return ans;
   }
   Mint operator -() const { return Mint().fix(MOD - val); }
   Mint inv() const { return pow(MOD - 2); }
   Mint operator / (const Mint &mt) const { return *this * mt.inv(); }
   Mint& operator /= (const Mint &mt) const { return *this / mt; }
   friend ostream& operator<<(ostream& os, const Mint& mt) { return os << mt.val; }
};

using Mint = ModInt<1000000007>;

const int MAXN = 1000100;

Mint fact[MAXN];
Mint ifact[MAXN];
Mint pow2[MAXN];
Mint pow3[MAXN];

void precalc() {
   fact[0] = 1;
   for (int i = 1; i < MAXN; ++i) fact[i] = fact[i - 1] * i;
   ifact[MAXN - 1] = fact[MAXN - 1].inv();
   for (int i = MAXN - 1; i > 0; --i) ifact[i - 1] = ifact[i] * i;
   pow2[0] = pow3[0] = 1;
   for (int i = 1; i < MAXN; ++i) {
      pow2[i] = pow2[i - 1] * 2;
      pow3[i] = pow3[i - 1] * 3;
   }
}

Mint C(int N, int K) {
   if (N < K || K < 0) return Mint(0);
   return fact[N] * ifact[K] * ifact[N - K];
}

int main() {
   ios_base::sync_with_stdio(false); cin.tie(nullptr);
   precalc();
   int N, M, K;
   cin >> N >> M >> K;
   vector<Mint> dpM(M + K + 1);
   vector<Mint> dpK(M + K + 1);
   dpM[0] = dpK[0] = 1;
   for (int i = 1; i <= M + K; ++i) {
      dpM[i] = dpM[i - 1] * 2;
      if (i > M) dpM[i] -= C(i - 1, M);
      dpK[i] = dpK[i - 1] * 2;
      if (i > K) dpK[i] -= C(i - 1, K);
   }
   Mint ans = 0;
   for (int i = 0; i <= M + K; ++i) {
      ans += C(N - 1 + i, i) * (dpM[i] + dpK[i] - pow2[i]) * pow3[M + K - i];
   }
   cout << ans << "\n";
   return 0;
}

