#define MOD 1000000007LL
#define max(x,y) ((x)>(y))?(x):(y)
#define min(x,y) ((x)>(y))?(y):(x)

long long comb_mod(int a, int b) {
	long long i, j, P, Q, R, EXP;
	if (a == b || b == 0) return 1;
	else {
		P = 1;
		for (i=1;i<=a;i++) {
			P *= i; P %= MOD;
		}
		Q = 1;
		for (j=1;j<=(max(b,a-b));j++) {
			R = j;
			EXP = 1000000005LL;
			while (EXP>0) {
				if (EXP & 1) Q *= R;
				R *= R;
				EXP >>= 1;
				R %= MOD; Q %= MOD;
			}
			if (j==(min(b,a-b))) {
				P *= Q; P %= MOD;
			}
		}
		P *= Q; P %= MOD;
		return P;
	}
}

int main() {
	int i, j, K, M, N, X, Y;
	long long P, Q, R;
	scanf("%d%d%d", &N, &M, &K);

	P = 0; 
	for (i=N;i<=N+M+K;i++) {
//		printf("%d\t", i);
		Q = 1;
		R = 3;
		j = N+M+K-i;
		while (j>0) {
			if (j & 1) Q *= R;
			R *= R;
			j >>= 1;
			R %= MOD;
			Q %= MOD;
		}
//		printf("%lld\t", Q);
		Q *= comb_mod(i-1, N-1);
		Q %= MOD;
//		printf("%lld\t", Q);
		R = 0;
		if (i-N == 0) R = 1;
		else for (j=(max(0,i-N-K));j<=(min(i-N,M));j++) {
			R += comb_mod(i-N,j);
			R %= MOD;
		}
		Q *= R;
		Q %= MOD;
//		printf("%lld\n", Q);

		P += Q;
	}

	printf("%lld\n", P);
	return 0;
}

