#define min(x,y) ((x)>(y))?(y):(x)

long long factinv[900010], fact[900010];

void factorial(void) {
	int i, j;
	long long P, Q, R, EXP;

	fact[0] = 1; factinv[0] = 1;
	for (i=1;i<=900000;i++) {
		fact[i] = fact[i-1];
		fact[i] *= i;
		fact[i] %= MOD;

		factinv[i] = factinv[i-1];
		P = 1;
		Q = i;
		EXP = 1000000005LL;
		while (EXP>0) {
			if (EXP & 1) P *= Q;
			Q *= Q;
			EXP >>= 1;
			P %= MOD;
			Q %= MOD;
		}
		factinv[i] *= P;
		factinv[i] %= MOD;
	}

}

long long comb_mod(int a, int b) {
	long long P=1;
	P = P * fact[a];
	P = P * factinv[a-b] % MOD;
	P = P * factinv[b]   % MOD;
	return P;
}

int main() {
	int i, j, K, M, N;
	long long P, Q, R, T;
	factorial();
	scanf("%d%d%d", &N, &M, &K);

	P = 0; T = 0;
	for (i=N;i<=N+M+K;i++) {
//		printf("%6d\t", i);
		Q = 1;
		R = 3;
		j = N+M+K-i;
		while (j>0) {
