			R *= R;
			j >>= 1;
			R %= MOD;
			Q %= MOD;
		}
//		printf("%10lld\t", Q);
		Q *= comb_mod(i-1, N-1);
		Q %= MOD;
//		printf("%10lld\t", Q);
		if (T==0) T = 1LL;
		else {
			T *= 2LL;
			if (i>N+K) T -= comb_mod(i-N-1, i-N-1-K);
			if (M<i-N) T -= comb_mod(i-N-1, M);
		}
		T %= MOD;
		if (T<0) T += MOD;
		Q *= T;
		Q %= MOD;
//		printf("%10lld\n", Q);

		P += Q;
		P %= MOD;
	}

	printf("%lld\n", P);
	return 0;
}

