	init();
	scanf("%d%d%d", &n, &m, &k);
	ans = 0, sum = 1;
	for (i = 0; i <= m + k; i++) {
		ans = (ans + sum * choose(n - 1 + i, n - 1) % MD * p3[m + k - i]) % MD;
		sum = (sum * 2 - choose(i, i - k) - choose(i, m)) % MD;
	}
	if (ans < 0)
		ans += MD;
	printf("%d\n", ans);
	return 0;
}

