class LargeSolver {
  final int n, m, k;
  ModCalculator mc;

  LargeSolver (int n, int m, int k) {
    this.n = n;
    this.m = m;
    this.k = k;
  }

  public long solve() {
    mc = new ModCalculator(1_000_000_007L);
    long answer = 0;
//      System.err.println();
    for (int x = 0; x <= m + k; x++) {
      // a [ax]* a
      long v1 = mc.getC(n + x - 1, x);
      long v2 = mc.pow(3, m + k - x);
      long v3 = mc.pow(2, x);
      long v = mc.mul(v1, mc.mul(v2, v3));
//      System.err.printf("%d %d %d %d\n", v1, v2, v3, v);
      answer = mc.add(answer, v);
    }
//    System.err.println(answer);
//      System.err.println();
      // b: m+1
      // x: m+1 -> m+k
    long vTemp = 0;
    for (int y = 1; y <= k; y++) {
      int x = y + m;
      long v1 = mc.getC(n + x - 1, x);
      long v2 = mc.pow(3, m + k - x);
      vTemp = mc.mul(vTemp, 2);
      vTemp = mc.add(vTemp, mc.getC(x - 1, m));
      long v = mc.mul(v1, mc.mul(v2, vTemp));
//      System.err.printf("%d %d %d %d\n", v1, v2, vTemp, v);
      answer = mc.sub(answer, v);
    }
//      System.err.println();
//    System.err.println(answer);
    vTemp = 0;
    for (int y = 1; y <= m; y++) {
      int x = y + k;
      long v1 = mc.getC(n + x - 1, x);
      long v2 = mc.pow(3, m + k - x);
      vTemp = mc.mul(vTemp, 2);
      vTemp = mc.add(vTemp, mc.getC(x - 1, m));
      long v = mc.mul(v1, mc.mul(v2, vTemp));
//      System.err.printf("%d %d %d %d\n", v1, v2, vTemp, v);
      answer = mc.sub(answer, v);
    }
//    System.err.println(answer);
    return answer;
  }
}

