  SmallSolver (int n, int m, int k) {
    this.n = n;
    this.m = m;
    this.k = k;
  }

  public long solve() {
    mc = new ModCalculator(1_000_000_007L);
    long answer = 0;
    for (int b = 0; b <= m; b++) {
