        // a [abc]* a
        long v1 = mc.getC(n + b + c - 1, b);
        long v2 = mc.getC(n + c - 1, c);
        long v3 = mc.pow(3, (m - b) + (k - c));
        long v = mc.mul(v1, mc.mul(v2, v3));
//        System.out.printf("%d %d %d %d\n", v1, v2, v3, v);
        answer = mc.add(answer, v);
      }
    }
    return answer;
  }
}

