        public void Solve()
        {
            var n = sc.Integer();
            var m = sc.Integer();
            var k = sc.Integer();
            var table = new ModTable(n + m + k + 1);

            ModInteger ans = 0;
            ModInteger C = 1;
            var l = 0;
            var r = 0;
            for (int i = n; i <= n + m + k; i++)
            {
                var a = table.Combination(i - 1, n - 1);
                a *= ModInteger.Pow(3, n + m + k - i);
                a *= C;
                ans += a;
                C *= 2;
                var rem = i - n + 1;
                Debug.WriteLine("{0} {1}", l, r);
                var nl = Math.Max(0, rem - k);
                var nr = Math.Min(m, rem);
                if (l != nl) C -= table.Combination(i - n, l);
                if (r == nr) C -= table.Combination(i - n, r);
                else if (r > nr) { C -= table.Combination(i - n, r) * 2; }
                l = nl;
                r = nr;
            }
            IO.Printer.Out.WriteLine(ans);

