public class Solver
{
    const int MOD = 1000000007;

    long[] f, rf, inv;
    void InitFacts(int size)
    {
        f = new long[size + 1];
        rf = new long[size + 1];
        inv = new long[size + 1];
        f[0] = f[1] = rf[0] = rf[1] = inv[1] = 1;
        for (int i = 2; i <= size; i++)
        {
            f[i] = f[i - 1] * i % MOD;
            inv[i] = inv[MOD % i] * (MOD - MOD / i) % MOD;
            rf[i] = inv[i] * rf[i - 1] % MOD;
        }
    }

    const int MAX = 10000;

    public void Solve()
    {
        int n = ReadInt();
        int m = ReadInt();
        int k = ReadInt();

        if (n > 1000 || m > 1000 || k > 1000)
            throw new Exception();

        InitFacts(MAX);
        var pow = new long[MAX];
        pow[0] = 1;
        for (int i = 1; i < MAX; i++)
            pow[i] = pow[i - 1] * 3 % MOD;

        long ans = 0;
        for (int len = n; len <= n + m + k; len++)
        {
            for (int i = 0; i <= m; i++)
            {
                int j = len - n - i;
                if (j < 0 || j > k)
                    continue;
                ans += f[len - 1] * rf[n - 1] % MOD * rf[i] % MOD * rf[j] % MOD * pow[n + m + k - len] % MOD;
            }
        }

        Write(ans % MOD);
    }

