fn run<R: BufRead, W: Write>(i: R, o: &mut W) {
    let mut i = AtRead::from(i);
    let nmk = i.read3::<usize, usize, usize>();
    writeln!(o, "{}", solve(nmk)).unwrap();
}
fn solve(n: (usize, usize, usize)) -> usize {
    let mut pows = Vec::new();
    let mut p = ModInt(1);
    for _ in 0..n.1 + n.2 + 1 {
        pows.push(p);
        p *= 3;
    }

    let n_sum = n.0 + n.1 + n.2;
    let facts = ModIntFacts::new(n_sum - 1);
    let mut result = ModInt(0);
    for i012 in n.0 - 1..n_sum {
        let i12 = i012 - (n.0 - 1);
        let mut c = ModInt(0);
        for i1 in 0..min(n.1, i12) + 1 {
            let i2 = i12 - i1;
            if i2 > n.2 {
                continue;
            }
            c += facts.comb(i12, i1);
        }
        c *= facts.comb(i012, n.0 - 1);
        c *= pows[n_sum - 1 - i012];
        result += c;
    }
    result.0
}

